mapper.insert:
insert into movie_Tag( movie_number, tag_table_num
) values(
#{movie_number},(select tag_table_num from (select tag_table_num from tag_table where tag_name = #{tag_name} order by tag_table_num desc) where rownum=1) )

mapper.select:

select count(*)
from tag_table
where tag_name=#{tag_name}
